// src/components/HomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HomePage = () => {
  const [comic, setComic] = useState('');
  const [title, setTitle] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5002/comic')
      .then(response => {
        console.log('Comic URL from API:', response.data.url);
        setComic(response.data.url);
        setTitle(response.data.title);
      })
      .catch(error => {
        console.error('Error fetching the comic:', error);
      });
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Lauren's Daily Comic Strip</h1>
      {title && <p>{title}</p>}
      <div style={styles.comicWrapper}>
        {comic && (
          <img
            src={comic}
            alt={title}
            style={styles.comic}
            onError={(e) => {
              console.error('Error loading dynamic comic image:', e);
              e.target.style.display = 'none';
            }}
            onLoad={() => console.log('Dynamic comic image loaded successfully')}
          />
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundColor: '#f0f0f0',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: '20px',
  },
  comicWrapper: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    maxWidth: '100%',
    maxHeight: '90vh',
  },
  comic: {
    maxWidth: '100%',
    maxHeight: '90vh',
    width: 'auto !important',     // Maintain aspect ratio
    height: 'auto !important',    // Maintain aspect ratio
  },
};

export default HomePage;
