// src/components/HomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HomePage = () => {
  const [comic, setComic] = useState('');
  const [title, setTitle] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5002/comic')
      .then(response => {
        setComic(response.data.url);
        setTitle(response.data.title);
      })
      .catch(error => {
        console.error('Error fetching the comic:', error);
      });
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Lauren's Daily Comic Strip</h1>
      {title && <p>{title}</p>}
      {comic && <img src={comic} alt={title} style={styles.comic} />}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundColor: '#f0f0f0',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: '20px',
  },
  comic: {
    maxWidth: '100%',
    maxHeight: '80%',
  },
};

export default HomePage;
