import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
  const [title, setTitle] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5002/comic')
      .then(response => {
        setTitle(response.data.title);
      })
      .catch(error => {
        console.error('Error fetching the comic title:', error);
      });
  }, []);

  const currentDate = new Date().toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  return (
    <div className="container">
      <h1 className="title">Lauren's Daily Comic Strip ‚ù§Ô∏è</h1>
      {title && <p className="comicTitle">{title}</p>}
      <div className="comicWrapper">
        <img
          src="http://localhost:5002/daily_comic"
          alt={title}
          className="comic"
          onError={(e) => {
            console.error('Error loading comic image:', e);
            e.target.style.display = 'none';
          }}
          onLoad={() => console.log('Comic image loaded successfully')}
        />
      </div>
      <p className="date">{currentDate}</p>
      <p className="message">"Here's a comic to brighten your day! Love you! üíñ"</p>
    </div>
  );
};

export default HomePage;
