// src/components/HomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
  const [title, setTitle] = useState('');
  const [loading, setLoading] = useState(true);
  const [comicUrl, setComicUrl] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5002/comic')
      .then(response => {
        setTitle(response.data.title);
        setComicUrl('http://localhost:5002/daily_comic');
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching the comic:', error);
        setLoading(false);
      });
  }, []);

  const shareComic = () => {
    const text = `Check out this comic: ${comicUrl}`;
    if (navigator.share) {
      navigator.share({
        title: 'Lauren\'s Daily Comic',
        text: text,
        url: comicUrl,
      });
    } else {
      navigator.clipboard.writeText(text).then(() => {
        alert('Comic URL copied to clipboard!');
      });
    }
  };

  return (
    <div className="container">
      <h1 className="title">Lauren's Daily</h1>
      {title && <p className="comicTitle">{title}</p>}
      <div className="comicWrapper">
        {loading ? (
          <div className="spinner"></div>
        ) : (
          <img
            src={comicUrl}
            alt={title}
            className="comic"
            onError={(e) => {
              console.error('Error loading comic image:', e);
              e.target.style.display = 'none';
            }}
            onLoad={() => console.log('Comic image loaded successfully')}
          />
        )}
      </div>
      {!loading && (
        <button className="shareButton" onClick={shareComic}>Share Comic</button>
      )}
      <p className="signature">Love, Jackson</p>
    </div>
  );
};

export default HomePage;
